gql:
query test($categories_pageInput: PageInput!) {
  users {
    user {
      categories(pageInput: $categories_pageInput) {
        categories {
          slug
          image
          image_thumbnail
          name
        }
      }
    }
  }
}
query servers($Servers_filter: ServersFilter!) {
  users {
    user {
      Servers(filter: $Servers_filter) {
        Servers {
          _id
          createdAt
          title
          description
          interestedUsers {
            username
            _id
            fullName
            avatarUrl
            banned
          }
        }
      }
    }
  }
}
query search($Servers_filter: ServersFilter!) {
  users {
    user {
      Servers(filter: $Servers_filter) {
        Servers {
          title
          interestedUsers {
            username
            _id
            fullName
            avatarUrl
          }
        }
      }
      me {
        avatarUrl
      }
    }
  }
}
query myServers($followedServers_pageInput: PageInput!) {
  users {
    user {
      me {
        _id
        username
        followedServers(pageInput: $followedServers_pageInput) {
          Servers {
            _id
            createdAt
            title
            description
            category {
              slug
              image
              image_thumbnail
            }
          }
        }
      }
    }
  }
}
mutation followServer(
  $followServer__id: String!
  $followServer_follow: Boolean
) {
  users {
    user {
      followServer(_id: $followServer__id, follow: $followServer_follow)
    }
  }
}
